import React from 'react';
import './App.css';
import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';

class App extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            tasks: [],
            isDisplayForm: false
        }
    }   
    // //khi load lai trang thi function dc call
    componentWillMount(){
        console.log(localStorage.getItem('tasks'));
        if(localStorage && localStorage.getItem('tasks')){
            var tasks = JSON.parse(localStorage.getItem('tasks'));
            this.setState({
                tasks: tasks
            })
        }
    }
    //tao du lieu dau 
    onGenerateData =()=>{
        var tasks = [
            {
                id: 1,
                name: 'Hoc Lap Trinh',
                status: true
            },
        ];
        this.setState({
            tasks: tasks
        })
        localStorage.setItem('tasks', JSON.stringify(tasks));
    }

    onToggleForm =()=>{
        this.setState({
            isDisplayForm: !this.state.isDisplayForm
        });
    }
    // kick nut x dong form 
    onCloseForm =()=>{
        this.setState({
            isDisplayForm: false
        });
    }
    //them du lieu 
    onSubmit = (data)=>{
        var {tasks} = this.state;

        var item = {
            id: 1 + (Math.random() * (1000-1)),
            name: data.name,
            status: data.status
        }
        tasks.push(item);
        this.setState({
            tasks: tasks
        });
        localStorage.setItem('tasks', JSON.stringify(tasks));

    }
    //kick status thi toggle
    onUpdateStatus =(id)=>{
        var index = this.findIndex(id);
    }
    findIndex = ()=>{

    }


    render(){
        const {tasks, isDisplayForm} = this.state;
        const elmTaskForm = isDisplayForm==true?<TaskForm onSubmit={this.onSubmit}  onCloseForm={this.onCloseForm}/> :'';
        return (
            <div className="container">
                <div className="text-center">
                    <h1>Quản Lý Công Việc</h1>
                    <hr/>
                </div>
                <div className="row">
                    <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                        {elmTaskForm}
                    </div>
                    <div className={isDisplayForm==true?'col-xs-8 col-sm-8 col-md-8 col-lg-8':
                    'col-xs-12 col-sm-12 col-md-12 col-lg-12'}>
                        <button 
                            type="button" 
                            className="btn btn-primary"
                            onClick={this.onToggleForm}
                        >
                            <span className="fa fa-plus mr-5"></span>Thêm Công Việc
                        </button>
                        <button 
                            type="button" 
                            className="btn btn-danger ml-5" 
                            onClick = {this.onGenerateData}
                        >
                            Generate Data
                        </button>
                        <Control />
                        <div className="row mt-15">
                            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                                <TaskList tasks={tasks} onUpdateStatus={this.onUpdateStatus}/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

}

export default App;
