import { createStore } from 'redux';

var initialState = {
    status: false,
    sort: {
        by: 'name',
        value: 1
    }

}

var myReducer = (state = initialState, action) => {
    if(action.type === 'TOGGLE_STATUS'){
        state.status = !state.sort;
    }
    return state;
}

//Thay doi status 
var action = {
    type : 'TOGGLE_STATUS'
}

StorageEvent.dispatch(action);
const store = createStore(myReducer);
