import { createStore } from 'redux';

var initialState = {
    status: false,
    sort: {
        by: 'name',
        value: 1
    }

}

var myReducer = (state = initialState, action) => {
    if(action.type === 'TOGGLE_STATUS'){
        state.status = !state.status;
    }
    if(action.type==='SORT'){
        var {by, value} = action;
        var {status} = state; //status = state.status
        state.sort = {
            by: action.sort.by,
            value: action.sort.value
